const { DateTime } = require('luxon');

// Pega os dados globais do primeiro n√≥ (s√≥ precisamos fazer isso uma vez).
const dadosGlobais = $('ConfirmGlobal').item.json;

const items = [];
for (const { json: ev } of $input.all()) {
  const start = ev.start?.dateTime || ev.start?.date;   // ISO
  const end   = ev.end?.dateTime   || ev.end?.date;

  // Filtra s√≥ convidados v√°lidos (evita organizer/self/resources/declined)
  const attendees = (ev.attendees || [])
    .filter(a => a?.email)
    .filter(a => !a.self && !a.resource && a.responseStatus !== 'declined');

  for (const a of attendees) {
    const dtStart = DateTime.fromISO(start).setZone('America/Sao_Paulo');
    const dtEnd   = DateTime.fromISO(end).setZone('America/Sao_Paulo');

    items.push({
      json: {
        // Contexto do evento
        nomeCompromisso: ev.summary || '',
        dataReuniao: dtStart.toFormat('dd/MM'),
        diaSemana: dtStart.setLocale('pt-BR').toFormat('cccc'),
        horaInicio: dtStart.toFormat('HH:mm'),
        horaFim: dtEnd.toFormat('HH:mm'),
        dataReuniaoISO: start,

        // Convidado alvo
        attendeeEmail: String(a.email).toLowerCase(),

        // --- DADOS GLOBAIS QUE ESTAMOS PASSANDO ADIANTE ---
        botName: dadosGlobais.botName,
        evolutionInstance: dadosGlobais.evolutionInstance
      }
    });
  }
}

return items;

// Carrega TODAS as listas de dados de que precisamos.
// $items() √© a fun√ß√£o correta para pegar todos os resultados de um n√≥.
const allEvents = $items("MeetVariables");
const allClients = $items("SearchClient");
const allConsultants = $items("SearchConsultant");

const finalItems = [];

// O loop principal DEVE ser sobre a lista de "tarefas", que √© a lista de convidados/eventos.
for (const eventItem of allEvents) {
  const dadosEvento = eventItem.json;
  const attendeeEmail = dadosEvento.attendeeEmail;

  // 1. Para este evento/convidado, encontre o registro do cliente correspondente.
  const clientItem = allClients.find(c => c.json.property_e_mail === attendeeEmail);

  // Se n√£o encontrarmos o cliente na nossa base, n√£o podemos continuar.
  if (!clientItem) {
    // console.log(`Cliente com email ${attendeeEmail} n√£o encontrado.`);
    continue;
  }
  const dadosCliente = clientItem.json;

  // 2. Agora que temos o cliente, pegue o ID do consultor respons√°vel por ele.
  const consultantId = dadosCliente.property_responsible_consultant ? dadosCliente.property_responsible_consultant[0] : null;

  // Se o cliente n√£o tiver um consultor, n√£o podemos continuar.
  if (!consultantId) {
    // console.log(`Cliente ${dadosCliente.name} n√£o tem consultor respons√°vel.`);
    continue;
  }

  // 3. Com o ID do consultor, encontre os detalhes completos do consultor.
  const consultantItem = allConsultants.find(c => c.json.id === consultantId);

  // Se, por algum motivo, n√£o encontrarmos os detalhes do consultor, n√£o continue.
  if (!consultantItem) {
    // console.log(`Detalhes do consultor com ID ${consultantId} n√£o encontrados.`);
    continue;
  }
  const dadosConsultor = consultantItem.json;

  // 4. SUCESSO! Encontramos todas as partes. Montamos o objeto final.
  finalItems.push({
    json: {
      // --- DADOS DO CONSULTOR ---
      consultantName: dadosConsultor.property_name,
      consultantPhone: dadosConsultor.property_phone,
      consultantEmail: dadosConsultor.property_email,

      // --- DADOS DO CLIENTE ---
      clienteName: dadosCliente.name,
      clienteEmail: dadosCliente.property_e_mail,
      clientePhone: dadosCliente.property_telefone,
      clienteChatId: dadosCliente.property_telefone ? dadosCliente.property_telefone.replace(/\D/g, '') + '@s.whatsapp.net' : '',
      clienteNick: dadosCliente.property_apelido_razao_social,

      // --- DADOS DO EVENTO ---
      nomeCompromisso: dadosEvento.nomeCompromisso,
      dataReuniao: dadosEvento.dataReuniao,
      diaSemana: dadosEvento.diaSemana,
      horaInicio: dadosEvento.horaInicio,
      horaFim: dadosEvento.horaFim,
      dataReuniaoISO: dadosEvento.dataReuniaoISO,
    }
  });
}

// Retorna a lista final com todos os itens corretamente combinados.
return finalItems;
